import React, { useState } from "react";

const App = () => {
  const [num1, setNum1] = useState("");
  const [num2, setNum2] = useState("");
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);

  const handleInputChange = (e, num) => {
    const value = e.target.value;

    // Validate the input (basic validation for demonstration)
    if (!value.trim().match(/^(\-|\+)?([0-9]+(\.[0-9]+)?)$/)) {
      setError("Invalid input. Please enter a valid number.");
    } else {
      setError(null);
      num === 1 ? setNum1(value) : setNum2(value);
    }
  };

  const handleOperation = (operation) => {
    // Basic arithmetic operations
    switch (operation) {
      case "+":
        setResult(parseFloat(num1) + parseFloat(num2));
        break;
      case "-":
        setResult(parseFloat(num1) - parseFloat(num2));
        break;
      case "*":
        setResult(parseFloat(num1) * parseFloat(num2));
        break;
      case "/":
        // Check for division by zero
        if (parseFloat(num2) === 0) {
          setError("Cannot divide by zero.");
          setResult(null);
        } else {
          setResult(parseFloat(num1) / parseFloat(num2));
          setError(null);
        }
        break;
      default:
        break;
    }
  };

  return (
    <div className="calculator">
      <h1>React Calculator</h1>
      <div>
        <input
          type="text"
          placeholder="Num1"
          value={num1}
          onChange={(e) => handleInputChange(e, '')}
        />
      </div>
      <div>
        <input
          type="text"
          placeholder="Num2"
          value={num2}
          onChange={(e) => handleInputChange(e, '')}
        />
      </div>
      <div id="inner-box">
        <div className="sign" onClick={() => handleOperation("+")}>+</div>
        <div className="sign" onClick={() => handleOperation("-")}>-</div>
        <div className="sign" onClick={() => handleOperation("*")}>*</div>
        <div className="sign" onClick={() => handleOperation("/")}>/</div>
      </div>
      {error && <div className="error">{error}</div>}
      {result !== null && <div className="success">Result: {result}</div>}
    </div>
  );
};

export default App;
